:root {
  /* Atlassian Design System (ADS) Base Tokens */

  /* Color Tokens */
  /* Blue Base Tokens */
  --ds-blue100: #e9f2fe;
  --ds-blue200: #cfe1fd;
  --ds-blue300: #8fb8f6;
  --ds-blue400: #669df1;
  --ds-blue500: #4688ec;
  --ds-blue600: #357de8;
  --ds-blue700: #1868db;
  --ds-blue800: #1558bc;
  --ds-blue900: #123263;
  --ds-blue1000: #1c2b42;

  /* Red Base Tokens */
  --ds-red100: #ffeceb;
  --ds-red200: #ffd5d2;
  --ds-red300: #fd9891;
  --ds-red400: #f87168;
  --ds-red500: #f15b50;
  --ds-red600: #e2483d;
  --ds-red700: #c9372c;
  --ds-red800: #ae2e24;
  --ds-red900: #5d1f1a;
  --ds-red1000: #42221f;

  /* Green Base Tokens */
  --ds-green100: #dcfff1;
  --ds-green200: #baf3db;
  --ds-green300: #7ee2b8;
  --ds-green400: #4bce97;
  --ds-green500: #2abb7f;
  --ds-green600: #22a06b;
  --ds-green700: #1f845a;
  --ds-green800: #216e4e;
  --ds-green900: #164b35;
  --ds-green1000: #1c3329;

  /* Yellow Base Tokens */
  --ds-yellow100: #fef7c8;
  --ds-yellow200: #f5e989;
  --ds-yellow300: #eed12b;
  --ds-yellow400: #ddb30e;
  --ds-yellow500: #cf9f02;
  --ds-yellow600: #b38600;
  --ds-yellow700: #946f00;
  --ds-yellow800: #7f5f01;
  --ds-yellow900: #533f04;
  --ds-yellow1000: #332e1b;

  /* Purple Base Tokens */
  --ds-purple100: #f8eefe;
  --ds-purple200: #eed7fc;
  --ds-purple300: #d8a0f7;
  --ds-purple400: #c97cf4;
  --ds-purple500: #bf63f3;
  --ds-purple600: #af59e1;
  --ds-purple700: #964ac0;
  --ds-purple800: #803fa5;
  --ds-purple900: #48245d;
  --ds-purple1000: #35243f;

  /* Teal Base Tokens */
  --ds-teal100: #e7f9ff;
  --ds-teal200: #c6edfb;
  --ds-teal300: #9dd9ee;
  --ds-teal400: #6cc3e0;
  --ds-teal500: #42b2d7;
  --ds-teal600: #2898bd;
  --ds-teal700: #227d9b;
  --ds-teal800: #206a83;
  --ds-teal900: #164555;
  --ds-teal1000: #1e3137;

  /* Orange Base Tokens */
  --ds-orange100: #fff5db;
  --ds-orange200: #fce4a6;
  --ds-orange300: #fbc828;
  --ds-orange400: #fca700;
  --ds-orange500: #f68909;
  --ds-orange600: #e06c00;
  --ds-orange700: #bd5b00;
  --ds-orange800: #9e4c00;
  --ds-orange900: #693200;
  --ds-orange1000: #3a2c1f;

  /* Magenta Base Tokens */
  --ds-magenta100: #ffecf8;
  --ds-magenta200: #fdd0ec;
  --ds-magenta300: #f797d2;
  --ds-magenta400: #e774bb;
  --ds-magenta500: #da62ac;
  --ds-magenta600: #cd519d;
  --ds-magenta700: #ae4787;
  --ds-magenta800: #943d73;
  --ds-magenta900: #50253f;
  --ds-magenta1000: #3d2232;

  /* Lime Base Tokens */
  --ds-lime100: #efffd6;
  --ds-lime200: #d3f1a7;
  --ds-lime300: #b3df72;
  --ds-lime400: #94c748;
  --ds-lime500: #82b536;
  --ds-lime600: #6a9a23;
  --ds-lime700: #5b7f24;
  --ds-lime800: #4c6b1f;
  --ds-lime900: #37471f;
  --ds-lime1000: #28311b;

  /* Neutral Base Tokens (Light Mode) */
  --ds-neutralminus100: #f8f8f8; /* Custom */
  --ds-neutralminus100a: #17171708; /* Custom = 3% opacity */
  --ds-neutral0: #ffffff;
  --ds-neutral100: #f8f8f8;
  --ds-neutral100a: #17171708; /* 3% opacity */
  --ds-neutral200: #f0f1f2;
  --ds-neutral200a: #0515240f; /* 6% opacity */
  --ds-neutral250: #f0f1f2; /* Custom */
  --ds-neutral250a: #0515240f; /* Custom = 6% opacity */
  --ds-neutral300: #dddee1;
  --ds-neutral300a: #0b120e24; /* 14% opacity */
  --ds-neutral350: #dddee1; /* Custom */
  --ds-neutral350a: #0b120e24; /* Custom = 14% opacity */
  --ds-neutral400: #b7b9be;
  --ds-neutral400a: #080f214a; /* 29% opacity */
  --ds-neutral500: #8c8f97;
  --ds-neutral500a: #050c1f75; /* 46% opacity */
  --ds-neutral600: #7d818a;
  --ds-neutral700: #6b6e76;
  --ds-neutral800: #505258;
  --ds-neutral900: #3b3d42;
  --ds-neutral1000: #292a2e;
  --ds-neutral1100: #1e1f21;

  /* Dark Neutral Base Tokens */
  --ds-darkneutralminus100: #111213;
  --ds-darkneutralminus100a: #01040475; /* 46% opacity */
  --ds-darkneutral0: #18191a;
  --ds-darkneutral100: #1f1f21;
  --ds-darkneutral100a: #bdbdbd0a; /* 4% opacity */
  --ds-darkneutral200: #242528;
  --ds-darkneutral200a: #ceced912; /* 7% opacity */
  --ds-darkneutral250: #2b2c2f;
  --ds-darkneutral250a: #d9dae71a; /* 10% opacity */
  --ds-darkneutral300: #303134;
  --ds-darkneutral300a: #e3e4f21f; /* 12% opacity */
  --ds-darkneutral350: #3d3f43;
  --ds-darkneutral350a: #e8edfd2e; /* 18% opacity */
  --ds-darkneutral400: #4b4d51;
  --ds-darkneutral400a: #e5e9f640; /* 25% opacity */
  --ds-darkneutral500: #63666b;
  --ds-darkneutral500a: #e9f0fb5c; /* 36% opacity */
  --ds-darkneutral600: #7e8188;
  --ds-darkneutral700: #96999e;
  --ds-darkneutral800: #a9abaf;
  --ds-darkneutral900: #bfc1c4;
  --ds-darkneutral1000: #cecfd2;
  --ds-darkneutral1100: #e2e3e4;

  /* Opacity Tokens */
  --ds-opacity20: 0.2;
  --ds-opacity40: 0.4;

  /* Shape Tokens */
  /* Size Tokens */
  --ds-size0: 0;
  --ds-size050: 1px;
  --ds-size100: 2px;
  --ds-size200: 3px;

  /* Border Radius Tokens */
  /* --ds-radius0: 0; */
  --ds-radius050: 2px;
  --ds-radius075: 3px;
  --ds-radius100: 4px;
  --ds-radius150: 6px;
  --ds-radius200: 8px;
  --ds-radius300: 12px;
  --ds-radius400: 16px;
  /* --ds-radiuscircle: 32032px; */

  /* Spacing Tokens */
  /* Positive Spacing */
  --ds-space0: 0;
  --ds-space025: 2px;
  --ds-space050: 4px;
  --ds-space075: 6px;
  --ds-space100: 8px;
  --ds-space150: 12px;
  --ds-space200: 16px;
  --ds-space250: 20px;
  --ds-space300: 24px;
  --ds-space400: 32px;
  --ds-space500: 40px;
  --ds-space600: 48px;
  --ds-space800: 64px;
  --ds-space1000: 80px;

  /* Negative Spacing */
  --ds-spacenegative025: -2px;
  --ds-spacenegative050: -4px;
  --ds-spacenegative075: -6px;
  --ds-spacenegative100: -8px;
  --ds-spacenegative150: -12px;
  --ds-spacenegative200: -16px;
  --ds-spacenegative250: -20px;
  --ds-spacenegative300: -24px;
  --ds-spacenegative400: -32px;

  /* Typography Tokens */
  /* Font Size Tokens */
  --ds-fontsizecode: 0.875em;
  --ds-fontsize12: 12px;
  --ds-fontsize14: 14px;
  --ds-fontsize16: 16px;
  --ds-fontsize20: 20px;
  --ds-fontsize24: 24px;
  --ds-fontsize28: 28px;
  --ds-fontsize32: 32px;
  --ds-fontsize36: 36px;
  --ds-fontsize48: 48px;

  /* Font Weight Tokens */
  --ds-fontweight400: 400;
  --ds-fontweight500: 500;
  --ds-fontweight600: 600;
  --ds-fontweight653: 653;
  --ds-fontweight700: 700;

  /* Font Family Tokens */
  --ds-fontfamilycharliedisplay: "Charlie Display", ui-sans-serif, -apple-system, BlinkMacSystemFont, "Segoe UI", Ubuntu, "Helvetica Neue", sans-serif;
  --ds-fontfamilycharlietext: "Charlie Text", ui-sans-serif, -apple-system, BlinkMacSystemFont, "Segoe UI", Ubuntu, "Helvetica Neue", sans-serif;
  --ds-fontfamilywebsans: "Atlassian Sans", ui-sans-serif, -apple-system, BlinkMacSystemFont, "Segoe UI", Ubuntu, "Helvetica Neue", sans-serif;
  --ds-fontfamilywebmono: "Atlassian Mono", ui-monospace, Menlo, "Segoe UI Mono", "Ubuntu Mono", monospace;
  --ds-fontfamilyiossans: SF Pro;
  --ds-fontfamilyandroidsans: Roboto;
  --ds-fontfamilyiosmono: SF Mono;
  --ds-fontfamilyandroidmono: Roboto Mono;

  /* Line Height Tokens */
  --ds-lineheight1: 1;
  --ds-lineheight16: 16;
  --ds-lineheight20: 20;
  --ds-lineheight24: 24;
  --ds-lineheight28: 28;
  --ds-lineheight32: 32;
  --ds-lineheight36: 36;
  --ds-lineheight40: 40;
  --ds-lineheight48: 48;
  --ds-lineheight56: 56;

  /* Letter Spacing Tokens */
  --ds-letterspacing0: 0;
  --ds-letterspacing100: -0.003em;
  --ds-letterspacing200: -0.006em;
  --ds-letterspacing300: -0.008em;
  --ds-letterspacing400: -0.01em;

  /* Atlassian Design System (ADS) Design Tokens */
  --ds-elevation-shadow-raised: 0 1px 1px rgba(9, 30, 66, 0.25), 0 0 1px rgba(9, 30, 66, 0.31);
  --ds-elevation-shadow-overflow: 0 0 8px rgba(9, 30, 66, 0.16), 0 0 1px rgba(9, 30, 66, 0.12);
  --ds-elevation-shadow-overlay: 0 8px 12px rgba(9, 30, 66, 0.15), 0 0 1px rgba(9, 30, 66, 0.31);

  --ds-dark-elevation-shadow-raised: 0 0 0 rgba(0, 0, 0, 0), 0 1px 1px rgba(3, 4, 4, 0.5), 0 0 1px rgba(3, 4, 4, 0.5);
  --ds-dark-elevation-shadow-overflow: 0 0 12px rgba(3, 4, 4, 0.56), 0 0 1px rgba(3, 4, 4, 0.5);
  --ds-dark-elevation-shadow-overlay: 0 0 0 rgba(57, 66, 74, 1), 0 8px 12px rgba(3, 4, 4, 0.36), 0 0 1px rgba(3, 4, 4, 0.5);

  /* Shadcn Tokens */
  --background: var(--ds-neutral0); /* Background color */
  --foreground: var(--ds-neutral1000); /* Text color */

  --card: var(--ds-neutral0);
  --card-foreground: var(--ds-neutral1000);

  --popover: var(--ds-neutral0);
  --popover-foreground: var(--ds-neutral1000);

  --primary: var(--ds-blue700);
  --primary-foreground: var(--ds-neutral0);

  --secondary: var(--ds-neutral200);
  --secondary-foreground: var(--ds-neutral900);

  --muted: var(--ds-neutral200);
  --muted-foreground: var(--ds-neutral700);

  --accent: var(--ds-neutral200);
  --accent-foreground: var(--ds-neutral900);

  --destructive: var(--ds-red600);

  --border: var(--ds-neutral300a);
  --input: var(--ds-neutral500);
  --ring: var(--ds-blue700);

  --chart-1: var(--ds-blue600);
  --chart-2: var(--ds-green600);
  --chart-3: var(--ds-orange600);
  --chart-4: var(--ds-purple600);
  --chart-5: var(--ds-teal600);

  --sidebar: var(--ds-neutral0); /* Background color */
  --sidebar-foreground: var(--ds-neutral1000); /* Text color */
  --sidebar-primary: var(--ds-blue700); /* Background color - Buttons in sidebar */
  --sidebar-primary-foreground: var(--ds-neutral0); /* Text color - Buttons in sidebar */
  --sidebar-accent: var(--ds-blue200); /* Hover state color - Selected item */
  --sidebar-accent-foreground: var(--ds-blue700); /* Hover state text color */
  --sidebar-border: var(--ds-neutral300a); /* Border color */
  --sidebar-ring: var(--ds-blue700); /* Focus state color */
}

.dark {
  --background: var(--ds-darkneutral0);
  --foreground: var(--ds-darkneutral1100);

  --card: var(--ds-darkneutral0);
  --card-foreground: var(--ds-darkneutral1100);

  --popover: var(--ds-darkneutral0);
  --popover-foreground: var(--ds-darkneutral1100);

  --primary: var(--ds-blue400);
  --primary-foreground: var(--ds-darkneutral0);

  --secondary: var(--ds-darkneutral200);
  --secondary-foreground: var(--ds-darkneutral900);

  --muted: var(--ds-darkneutral200);
  --muted-foreground: var(--ds-darkneutral700);

  --accent: var(--ds-darkneutral200);
  --accent-foreground: var(--ds-darkneutral900);

  --destructive: var(--ds-red500);

  --border: var(--ds-darkneutral300a);
  --input: var(--ds-darkneutral600);
  --ring: var(--ds-blue400);

  --chart-1: var(--ds-blue400);
  --chart-2: var(--ds-green400);
  --chart-3: var(--ds-orange400);
  --chart-4: var(--ds-purple400);
  --chart-5: var(--ds-teal400);

  --sidebar: var(--ds-darkneutral0);
  --sidebar-foreground: var(--ds-darkneutral1000);
  --sidebar-primary: var(--ds-blue900);
  --sidebar-primary-foreground: var(--ds-darkneutral0);
  --sidebar-accent: var(--ds-blue900);
  --sidebar-accent-foreground: var(--ds-blue400);
  --sidebar-border: var(--ds-darkneutral300a);
  --sidebar-ring: var(--ds-blue400);
}

@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:where(.dark, .dark *));

@theme inline {
  /* Tailwind Tokens */
  /* Blue Base Tailwind Tokens */
  --color-blue-50: var(--ds-blue100); /* Light mode: blue100, Dark mode: blue1000 */
  --color-blue-100: var(--ds-blue200); /* Light mode: blue200, Dark mode: blue900 */
  --color-blue-200: var(--ds-blue300); /* Light mode: blue300, Dark mode: blue800 */
  --color-blue-300: var(--ds-blue400); /* Light mode: blue400, Dark mode: blue700 */
  --color-blue-400: var(--ds-blue500); /* Light mode: blue500, Dark mode: blue600 */
  --color-blue-500: var(--ds-blue600); /* Light mode: blue600, Dark mode: blue500 */
  --color-blue-600: var(--ds-blue700); /* Light mode: blue700, Dark mode: blue400 */
  --color-blue-700: var(--ds-blue800); /* Light mode: blue800, Dark mode: blue300 */
  --color-blue-800: var(--ds-blue900); /* Light mode: blue900, Dark mode: blue200 */
  --color-blue-900: var(--ds-blue1000); /* Light mode: blue1000, Dark mode: blue100 */
  --color-blue-950: var(--ds-blue1000); /* Light mode: blue1000, Dark mode: blue100 */

  /* Red Base Tailwind Tokens */
  --color-red-50: var(--ds-red100); /* Light mode: red100, Dark mode: red1000 */
  --color-red-100: var(--ds-red200); /* Light mode: red200, Dark mode: red900 */
  --color-red-200: var(--ds-red300); /* Light mode: red300, Dark mode: red800 */
  --color-red-300: var(--ds-red400); /* Light mode: red400, Dark mode: red700 */
  --color-red-400: var(--ds-red500); /* Light mode: red500, Dark mode: red600 */
  --color-red-500: var(--ds-red600); /* Light mode: red600, Dark mode: red500 */
  --color-red-600: var(--ds-red700); /* Light mode: red700, Dark mode: red400 */
  --color-red-700: var(--ds-red800); /* Light mode: red800, Dark mode: red300 */
  --color-red-800: var(--ds-red900); /* Light mode: red900, Dark mode: red200 */
  --color-red-900: var(--ds-red1000); /* Light mode: red1000, Dark mode: red100 */
  --color-red-950: var(--ds-red1000); /* Light mode: red1000, Dark mode: red100 */

  /* Green Base Tailwind Tokens */
  --color-green-50: var(--ds-green100); /* Light mode: green100, Dark mode: green1000 */
  --color-green-100: var(--ds-green200); /* Light mode: green200, Dark mode: green900 */
  --color-green-200: var(--ds-green300); /* Light mode: green300, Dark mode: green800 */
  --color-green-300: var(--ds-green400); /* Light mode: green400, Dark mode: green700 */
  --color-green-400: var(--ds-green500); /* Light mode: green500, Dark mode: green600 */
  --color-green-500: var(--ds-green600); /* Light mode: green600, Dark mode: green500 */
  --color-green-600: var(--ds-green700); /* Light mode: green700, Dark mode: green400 */
  --color-green-700: var(--ds-green800); /* Light mode: green800, Dark mode: green300 */
  --color-green-800: var(--ds-green900); /* Light mode: green900, Dark mode: green200 */
  --color-green-900: var(--ds-green1000); /* Light mode: green1000, Dark mode: green100 */
  --color-green-950: var(--ds-green1000); /* Light mode: green1000, Dark mode: green100 */

  /* Yellow Base Tailwind Tokens */
  --color-yellow-50: var(--ds-yellow100); /* Light mode: yellow100, Dark mode: yellow1000 */
  --color-yellow-100: var(--ds-yellow200); /* Light mode: yellow200, Dark mode: yellow900 */
  --color-yellow-200: var(--ds-yellow300); /* Light mode: yellow300, Dark mode: yellow800 */
  --color-yellow-300: var(--ds-yellow400); /* Light mode: yellow400, Dark mode: yellow700 */
  --color-yellow-400: var(--ds-yellow500); /* Light mode: yellow500, Dark mode: yellow600 */
  --color-yellow-500: var(--ds-yellow600); /* Light mode: yellow600, Dark mode: yellow500 */
  --color-yellow-600: var(--ds-yellow700); /* Light mode: yellow700, Dark mode: yellow400 */
  --color-yellow-700: var(--ds-yellow800); /* Light mode: yellow800, Dark mode: yellow300 */
  --color-yellow-800: var(--ds-yellow900); /* Light mode: yellow900, Dark mode: yellow200 */
  --color-yellow-900: var(--ds-yellow1000); /* Light mode: yellow1000, Dark mode: yellow100 */
  --color-yellow-950: var(--ds-yellow1000); /* Light mode: yellow1000, Dark mode: yellow100 */

  /* Purple Base Tailwind Tokens */
  --color-purple-50: var(--ds-purple100); /* Light mode: purple100, Dark mode: purple1000 */
  --color-purple-100: var(--ds-purple200); /* Light mode: purple200, Dark mode: purple900 */
  --color-purple-200: var(--ds-purple300); /* Light mode: purple300, Dark mode: purple800 */
  --color-purple-300: var(--ds-purple400); /* Light mode: purple400, Dark mode: purple700 */
  --color-purple-400: var(--ds-purple500); /* Light mode: purple500, Dark mode: purple600 */
  --color-purple-500: var(--ds-purple600); /* Light mode: purple600, Dark mode: purple500 */
  --color-purple-600: var(--ds-purple700); /* Light mode: purple700, Dark mode: purple400 */
  --color-purple-700: var(--ds-purple800); /* Light mode: purple800, Dark mode: purple300 */
  --color-purple-800: var(--ds-purple900); /* Light mode: purple900, Dark mode: purple200 */
  --color-purple-900: var(--ds-purple1000); /* Light mode: purple1000, Dark mode: purple100 */
  --color-purple-950: var(--ds-purple1000); /* Light mode: purple1000, Dark mode: purple100 */

  /* Teal Base Tailwind Tokens */
  --color-teal-50: var(--ds-teal100); /* Light mode: teal100, Dark mode: teal1000 */
  --color-teal-100: var(--ds-teal200); /* Light mode: teal200, Dark mode: teal900 */
  --color-teal-200: var(--ds-teal300); /* Light mode: teal300, Dark mode: teal800 */
  --color-teal-300: var(--ds-teal400); /* Light mode: teal400, Dark mode: teal700 */
  --color-teal-400: var(--ds-teal500); /* Light mode: teal500, Dark mode: teal600 */
  --color-teal-500: var(--ds-teal600); /* Light mode: teal600, Dark mode: teal500 */
  --color-teal-600: var(--ds-teal700); /* Light mode: teal700, Dark mode: teal400 */
  --color-teal-700: var(--ds-teal800); /* Light mode: teal800, Dark mode: teal300 */
  --color-teal-800: var(--ds-teal900); /* Light mode: teal900, Dark mode: teal200 */
  --color-teal-900: var(--ds-teal1000); /* Light mode: teal1000, Dark mode: teal100 */
  --color-teal-950: var(--ds-teal1000); /* Light mode: teal1000, Dark mode: teal100 */

  /* Orange Base Tailwind Tokens */
  --color-orange-50: var(--ds-orange100); /* Light mode: orange100, Dark mode: orange1000 */
  --color-orange-100: var(--ds-orange200); /* Light mode: orange200, Dark mode: orange900 */
  --color-orange-200: var(--ds-orange300); /* Light mode: orange300, Dark mode: orange800 */
