  --color-orange-800: var(--ds-orange900); /* Light mode: orange900, Dark mode: orange200 */
  --color-orange-900: var(--ds-orange1000); /* Light mode: orange1000, Dark mode: orange100 */
  --color-orange-950: var(--ds-orange1000); /* Light mode: orange1000, Dark mode: orange100 */

  /* Pink Base Tailwind Tokens (ADS's Magenta) */
  --color-pink-50: var(--ds-magenta100); /* Light mode: magenta100, Dark mode: magenta1000 */
  --color-pink-100: var(--ds-magenta200); /* Light mode: magenta200, Dark mode: magenta900 */
  --color-pink-200: var(--ds-magenta300); /* Light mode: magenta300, Dark mode: magenta800 */
  --color-pink-300: var(--ds-magenta400); /* Light mode: magenta400, Dark mode: magenta700 */
  --color-pink-400: var(--ds-magenta500); /* Light mode: magenta500, Dark mode: magenta600 */
  --color-pink-500: var(--ds-magenta600); /* Light mode: magenta600, Dark mode: magenta500 */
  --color-pink-600: var(--ds-magenta700); /* Light mode: magenta700, Dark mode: magenta400 */
  --color-pink-700: var(--ds-magenta800); /* Light mode: magenta800, Dark mode: magenta300 */
  --color-pink-800: var(--ds-magenta900); /* Light mode: magenta900, Dark mode: magenta200 */
  --color-pink-900: var(--ds-magenta1000); /* Light mode: magenta1000, Dark mode: magenta100 */
  --color-pink-950: var(--ds-magenta1000); /* Light mode: magenta1000, Dark mode: magenta100 */

  /* Lime Base Tailwind Tokens */
  --color-lime-50: var(--ds-lime100); /* Light mode: lime100, Dark mode: lime1000 */
